import React from 'react'
import { StyleSheet, View, Text } from 'react-native'
import { w } from '../constants'

const SubjectItem = ({ subject }) => {
  const coupleTypeColor =
    subject.coupleType === 'ЛЗ'
      ? styles.redColor
      : subject.coupleType === 'ЛК'
      ? styles.lightGreenColor
      : subject.coupleType === 'ПЗ'
      ? styles.lightyellow
      : {}
  return (
    <>
      <View style={styles.container}>
        <Text>{subject.time} </Text>
        <View>
          <View style={styles.coupleContainer}>
            <Text style={styles.whiteColor}>{subject.coupleName}</Text>
            <Text style={coupleTypeColor}> ({subject.coupleType})</Text>
          </View>
          <Text style={styles.coupleLocation}>{subject.coupleLocation}</Text>
        </View>
        <View style={styles.teacherNameContainer}>
          <Text style={styles.teacherName}>{subject.teacherName} </Text>
        </View>
      </View>
    </>
  )
}

const styles = StyleSheet.create({
  lightGreenColor: {
    color: 'green'
  },
  redColor: {
    color: 'red'
  },
  lightYellowColor: {
    color: 'lightyellow'
  },
  whiteColor: {
    color: 'white'
  },
  coupleContainer: {
    flexDirection: 'row'
  },
  container: {
    flex: 1,
    flexDirection: 'row',
    backgroundColor: '#8b00cc',
    padding: 12,
    height: 63,
    margin: 10,
    marginTop: 5,
    marginBottom: 0,
    overflow: 'hidden',
    position: 'relative'
  },
  coupleLocation: {
    color: 'white',
    fontSize: 12
  },
  teacherNameContainer: {
    position: 'absolute',
    right: 5,
    bottom: 5
  },
  teacherName: {
    color: 'white',
    fontSize: 12,
    flexWrap: 'wrap',
    width: 100
  }
})

export default SubjectItem
