import React from 'react'
import { StyleSheet, View, Text } from 'react-native'
import { w } from '../constants'

const SubjectItem = ({ subject }) => {
  return (
    <>
      <View style={styles.container}>
        <Text>{subject.time} </Text>
        <View>
          <Text style={styles.whiteColor}>
            {subject.coupleName} ({subject.coupleType})
          </Text>
          <Text style={styles.coupleLocation}>{subject.coupleLocation}</Text>
        </View>
        <View style={styles.teacherNameContainer}>
          <Text style={styles.teacherName}>{subject.teacherName} </Text>
        </View>
      </View>
    </>
  )
}

const styles = StyleSheet.create({
  lightGreenColor: {

  },
  redColor: {

  },
  lightYellowColor: {

  },
  whiteColor: {
    color: 'white'
  },
  container: {
    flex: 1,
    flexDirection: 'row',
    backgroundColor: '#8b00cc',
    padding: 12,
    height: 63,
    margin: 10,
    marginTop: 5,
    marginBottom: 0,
    overflow: 'hidden',
    position: 'relative'
  },
  coupleLocation: {
    color: 'white',
    fontSize: 12
  },
  teacherNameContainer: {
    position: 'absolute',
    right: 5,
    bottom: 5
  },
  teacherName: {
    color: 'white',
    fontSize: 12,
    flexWrap: 'wrap',
    width: 100
  }
})

export default SubjectItem
